(function(){var a={};
a.CSS_PREFIX=".cqview .module.rss_wrapper ";
a.name="News";
a.items=$();
a.init=function(){shell.registerComponent(a);
$(document).ready(function(){$(a.CSS_PREFIX).each(function(){var b=$(this);
b.find(".items").each(function(){var c=b.find(".pager");
$(this).on("slideinit",function(e,g,d){var f=$("<a>");
f.attr("href","#");
f.attr("aria-label","Change slide to "+(d+1));
c.append(f);
f.on("click",function(h){g.changeSlide(d);
$(this).siblings().removeClass("shell_carousel_current");
$(this).addClass("shell_carousel_current");
h.preventDefault()
})
});
$(this).on("slidechanged",function(e,g){var d=g.xdata.currentSlide;
var f=b.find(".pager a");
f.removeClass("shell_carousel_current");
f.eq(d).addClass("shell_carousel_current")
});
c.fadeIn();
$(this).xlider({auto:false,debug:false,mouseSlide:false})
})
})
})
};
a.init()
})();